-- ----------------------------
-- Table structure for biz_order
-- ----------------------------
DROP TABLE IF EXISTS `biz_order`;
CREATE TABLE `biz_order` (
                             `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT COMMENT '订单ID',
                             `shop_id` bigint(20) NOT NULL COMMENT '店铺id',
                             `prod_name` varchar(1000) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '产品名称,多个产品将会以逗号隔开',
                             `user_id` bigint(20) NOT NULL COMMENT '用户ID',
                             `order_number` varchar(50) CHARACTER SET utf8 NOT NULL COMMENT '订购流水号，biz系统生成',
                             `total` decimal(15,2) NOT NULL DEFAULT '0.00' COMMENT '订单总金额',
                             `pay_type` int(1) DEFAULT NULL COMMENT '支付方式 0 手动代付 1 微信支付 2 支付宝',
                             `remarks` varchar(1024) CHARACTER SET utf8 DEFAULT NULL COMMENT '订单备注',
                             `status` int(2) NOT NULL DEFAULT '0' COMMENT '订单状态 1:待付款 3:成功 4:失败',
                             `product_nums` int(10) DEFAULT NULL COMMENT '订单商品总数',
                             `create_time` datetime NOT NULL COMMENT '订购时间',
                             `update_time` datetime DEFAULT NULL COMMENT '订单更新时间',
                             `pay_time` datetime DEFAULT NULL COMMENT '付款时间',
                             `finally_time` datetime DEFAULT NULL COMMENT '完成时间',
                             `cancel_time` datetime DEFAULT NULL COMMENT '取消时间',
                             `is_payed` tinyint(1) DEFAULT NULL COMMENT '是否已经支付，1：已经支付过，0：，没有支付过',
                             `delete_status` int(1) DEFAULT '0' COMMENT '用户订单删除状态，0：没有删除， 1：回收站， 2：永久删除',
                             `refund_status` int(1) DEFAULT '0' COMMENT '0:默认,1:在处理,2:处理完成',
                             `close_type` tinyint(2) DEFAULT NULL COMMENT '订单关闭原因 1-超时未支付 2-退款关闭 4-买家取消',
                             `order_source` int(11) DEFAULT NULL COMMENT '订单来源 1：微信小程序 2： 微信公众号 3：支付宝小程序',
                             `meal_time` varchar(255) DEFAULT NULL COMMENT '取餐时间',
                             `meal_number` varchar(50) DEFAULT NULL COMMENT '取餐号',
                             `meal_type` int(11) DEFAULT NULL COMMENT '就餐方式 1：堂食 2：打包',
                             PRIMARY KEY (`id`),
                             UNIQUE KEY `order_number_unique` (`order_number`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=1603660554170114051 DEFAULT CHARSET=utf8mb4 COMMENT='订单';

