-- ----------------------------
-- Table structure for biz_order_item
-- ----------------------------
DROP TABLE IF EXISTS `biz_order_item`;
CREATE TABLE `biz_order_item` (
                                  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT COMMENT '订单项ID',
                                  `shop_id` bigint(20) NOT NULL COMMENT '店铺id',
                                  `order_id` bigint(20) NOT NULL COMMENT '订单ID\n',
                                  `product_id` bigint(20) unsigned NOT NULL COMMENT '产品ID',
                                  `sku_id` bigint(20) unsigned NOT NULL COMMENT '产品SkuID',
                                  `product_count` int(11) NOT NULL DEFAULT '0' COMMENT '产品个数',
                                  `product_name` varchar(120) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '产品名称',
                                  `sku_name` varchar(120) CHARACTER SET utf8 DEFAULT NULL COMMENT 'sku名称',
                                  `pic` varchar(255) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '产品主图片路径',
                                  `price` decimal(15,2) NOT NULL COMMENT '产品价格',
                                  `user_id` bigint(20) NOT NULL COMMENT '用户Id',
                                  `product_total_amount` decimal(15,2) NOT NULL COMMENT '商品总金额',
                                  `basket_date` datetime DEFAULT NULL COMMENT '加入购物车时间',
                                  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1603660554224640002 DEFAULT CHARSET=utf8mb4 COMMENT='订单项';

