-- ----------------------------
-- Table structure for biz_order_refund
-- ----------------------------
DROP TABLE IF EXISTS `biz_order_refund`;
CREATE TABLE `biz_order_refund` (
                                    `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '记录ID',
                                    `shop_id` bigint(20) NOT NULL COMMENT '店铺ID',
                                    `order_id` bigint(20) NOT NULL COMMENT '订单ID',
                                    `order_number` varchar(50) CHARACTER SET utf8 NOT NULL COMMENT '订单流水号',
                                    `order_amount` decimal(10,2) NOT NULL COMMENT '订单总金额',
                                    `order_item_id` varchar(1024) NOT NULL DEFAULT '0' COMMENT '订单项ID 全部退款是0',
                                    `refund_number` varchar(50) CHARACTER SET utf8 NOT NULL COMMENT '退款编号',
                                    `pay_no` varchar(100) CHARACTER SET utf8 NOT NULL COMMENT '订单支付流水号',
                                    `refund_id` varchar(200) CHARACTER SET utf8 DEFAULT NULL COMMENT '微信退款单号',
                                    `pay_type` int(1) DEFAULT NULL COMMENT '订单支付方式 1 微信支付 2 支付宝',
                                    `pay_type_name` varchar(50) CHARACTER SET utf8 DEFAULT NULL COMMENT '订单支付名称',
                                    `user_id` bigint(20) NOT NULL COMMENT '买家ID',
                                    `goods_num` int(11) DEFAULT NULL COMMENT '退货数量',
                                    `refund_amount` decimal(10,2) NOT NULL COMMENT '退款金额',
                                    `return_money_sts` int(1) NOT NULL DEFAULT '0' COMMENT '处理退款状态: 0:退款处理中 1:退款成功 -1:退款失败',
                                    `create_time` datetime NOT NULL COMMENT '申请时间',
                                    `refund_time` datetime DEFAULT NULL COMMENT '退款时间',
                                    `buyer_msg` varchar(300) CHARACTER SET utf8 DEFAULT NULL COMMENT '申请原因',
                                    `funds_account` varchar(255) DEFAULT NULL COMMENT '退款所使用资金对应的资金账户类型\n枚举值：\nUNSETTLED : 未结算资金\nAVAILABLE : 可用余额\nUNAVAILABLE : 不可用余额\nOPERATION : 运营户\nBASIC : 基本账户（含可用余额和不可用余额）',
                                    `user_received_account` varchar(255) DEFAULT NULL COMMENT '退款入账账户',
                                    PRIMARY KEY (`id`),
                                    UNIQUE KEY `refund_sn_unique` (`refund_number`) USING BTREE,
                                    KEY `order_number` (`order_number`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=1602462463297699842 DEFAULT CHARSET=utf8mb4 COMMENT='支付退款';

