DROP TABLE IF EXISTS `biz_shop`;
CREATE TABLE `biz_shop` (
                            `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '店铺id',
                            `shop_name` varchar(50) CHARACTER SET utf8 NOT NULL COMMENT '店铺名称(数字、中文，英文(可混合，不可有特殊字符)，可修改)、不唯一',
                            `user_id` varchar(36) CHARACTER SET utf8 DEFAULT NULL COMMENT '店长用户id',
                            `shop_owner` varchar(20) CHARACTER SET utf8 DEFAULT NULL COMMENT '店长',
                            `mobile` varchar(20) CHARACTER SET utf8 NOT NULL COMMENT '店铺绑定的手机(登录账号：唯一)',
                            `shop_notice` varchar(50) CHARACTER SET utf8 DEFAULT NULL COMMENT '店铺公告(可修改)',
                            `intro` varchar(200) CHARACTER SET utf8 DEFAULT NULL COMMENT '店铺简介(可修改)',
                            `tel` varchar(20) CHARACTER SET utf8 DEFAULT NULL COMMENT '店铺联系电话',
                            `shop_lat` varchar(20) CHARACTER SET utf8 NOT NULL COMMENT '店铺所在纬度(可修改)',
                            `shop_lng` varchar(20) CHARACTER SET utf8 NOT NULL COMMENT '店铺所在经度(可修改)',
                            `shop_address` varchar(100) CHARACTER SET utf8 DEFAULT NULL COMMENT '店铺详细地址',
                            `province` varchar(10) CHARACTER SET utf8 DEFAULT NULL COMMENT '店铺所在省份（描述）',
                            `city` varchar(10) CHARACTER SET utf8 DEFAULT NULL COMMENT '店铺所在城市（描述）',
                            `area` varchar(10) CHARACTER SET utf8 DEFAULT NULL COMMENT '店铺所在区域（描述）',
                            `shop_logo` varchar(200) CHARACTER SET utf8 NOT NULL COMMENT '店铺logo(可修改)',
                            `shop_photos` varchar(1000) CHARACTER SET utf8 DEFAULT NULL COMMENT '店铺相册',
                            `open_time` varchar(100) CHARACTER SET utf8 DEFAULT NULL COMMENT '每天营业时间段(可修改)',
                            `shop_status` tinyint(2) DEFAULT NULL COMMENT '店铺状态(-1:未开通 0: 停业中 1:营业中)，可修改',
                            `creator` bigint(20) DEFAULT NULL COMMENT '创建者',
                            `create_date` datetime DEFAULT NULL COMMENT '创建时间',
                            `updater` bigint(20) DEFAULT NULL COMMENT '更新者',
                            `update_date` datetime DEFAULT NULL COMMENT '更新时间',
                            PRIMARY KEY (`id`),
                            UNIQUE KEY `mobile_unique` (`mobile`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=1598210656903983107 DEFAULT CHARSET=utf8mb4 COMMENT='店铺';

